import React, {useState} from 'react';
import '../components/Background.css';
import NavigationBar from '../components/NavigationBar';
import 'bootstrap/dist/css/bootstrap.css';

const BASE_URL = 'https://cop4331mern.herokuapp.com/';

function TestForm() {
    var loginName;
    var password;

    const [message,setMessage] = useState('');
    const doLogin = async event => 
    {
        event.preventDefault();
        alert( loginName.value + ' ' + password.value);
    }

    return(
<div class="container">
    <div class="row">
        <div class="col-lg-6 col-md-7 mx-auto">
            <form onSubmit={doLogin} class="form-signin">
                <div class="text-center mb-4">
                    <h1 class="h3 mb-3 font-weight-normal">Login</h1>
                </div>
               
                <div class="form-label-group">
                    <input type="text" id="loginName" class="form-control" placeholder="Username" required="" autofocus="" />
                    <label for="loginName">Username</label>
                </div>
               
                <div class="form-label-group">
                    <input type="password" id="password" class="form-control" placeholder="Password" required="" />
                    <label for="password">Password</label>
                </div>
               
                <button class="btn btn-lg btn-primary btn-block" type="submit" onClick={doLogin}>Login</button>
            </form>
        </div>
    </div>
</div>

    );
};

export default TestForm;
