import React, {useState, Component} from 'react';
import '../components/Background.css';
import NavigationBar from '../components/NavigationBar';
import 'bootstrap/dist/css/bootstrap.css';

const regExp = RegExp(
    /^[a-zA-Z0-9]+@[a-zA-Z0-9]+\.[A-Za-z]+$/
)

const formValid = ({ isError, ...rest }) => {
    let isValid = false;

    Object.values(isError).forEach(val => {
        if (val.length > 0) {
            isValid = false
        } else {
            isValid = true
        }
    });

    Object.values(rest).forEach(val => {
        if (val === null) {
            isValid = false
        } else {
            isValid = true
        }
    });

    return isValid;
};


var loginName;
var password;

export default class UserForm extends Component {
    constructor(props) {
        super(props)

        this.state = {
            name: '',
            email: '',
            password: '',
            isError: {
                name: '',
                email: '',
                password: ''
            }
        }
    }

    onSubmit = e => {
        e.preventDefault();

        if (formValid(this.state)) {
            console.log(this.state)
        } else {
            console.log("Form is invalid!");
        }

        alert( loginName.value + ' ' + password.value);
    };


    formValChange = e => {
        e.preventDefault();
        const { name, value } = e.target;
        let isError = { ...this.state.isError };

        switch (name) {
            case "loginName":
                isError.name =
                    value.length == 0 ? "Invalid Name" : "";
                break;
            case "email":
                isError.email = regExp.test(value)
                    ? ""
                    : "Invalid Email";
                break;
            case "password":
                isError.password =
                    value.length == 0 ? "Invalid Password" : "";
                break;
            default:
                break;
        }

        this.setState({
            isError,
            [name]: value
        })
    };

    render() {
        const { isError } = this.state;

        return (
            <div id="loginDiv">
            <NavigationBar />
            <div class="container">
            <div class="row">
            <div class="col-lg-6 col-md-7 mx-auto">
            <form onSubmit={this.onSubmit} class="form-signin" noValidate>
            <div class="text-center mb-4">
            <br /> <h1>Login</h1>
            </div>

            <div class="form-label-group">
                <input type="text" id="loginName" class="form-control" placeholder="Username" required="" autofocus="" ref={(c) => loginName = c} 
                className={isError.name.length > 0 ? "is-invalid form-control" : "form-control"} name="loginName" onChange={this.formValChange}/>
                 {isError.name.length > 0 && (
                        <span className="invalid-feedback">{isError.name}</span>
                    )}
                <label for="loginName">Username</label>
            </div>

            <div class="form-label-group">
                <input type="password" id="password" class="form-control" placeholder="Password" required="" ref={(c) => password = c}
                className={isError.password.length > 0 ? "is-invalid form-control" : "form-control"}
                name="password" onChange={this.formValChange}/>
                {isError.password.length > 0 && (
                        <span className="invalid-feedback">{isError.password}</span>
                )}
                <label for="password">Password</label>
            </div>  

            <button class="btn btn-lg btn-primary btn-block" type="submit" onClick={this.onSubmit}>Login</button>
            </form>
        </div>
    </div>
</div>
</div>

        );
    }
}
