import React, {useState} from 'react';
import '../components/Background.css';
import NavigationBar from '../components/NavigationBar';
import 'bootstrap/dist/css/bootstrap.css';

const BASE_URL = 'https://cop4331mern.herokuapp.com/';

function RegisterForm() {
    var loginName;
    var firstName;
    var lastName;
    var email;
    var loginPassword;
    var confirmPassword;

    const [message,setMessage] = useState('');
    const doRegister = async event => 
    {
        event.preventDefault();
        alert( loginName.value + ' ' + firstName.value + ' ' + lastName.value + ' ' + email.value + ' ' + loginPassword.value + ' ' + loginPassword.value );
    }

return(

 <div id="registerDiv">
<NavigationBar />
  <form onSubmit={doRegister}>
    <h1> REGISTER </h1> <br />
    <input type="text" id="loginName" placeholder="Username" ref={(c) => loginName = c}  /><br /><br />
    <input type="password" id="loginPassword" placeholder="Password" ref={(c) => loginPassword = c} /><br /><br />
    <input type="password" id="confirmPassword" placeholder="Confirm Password" ref={(c) => confirmPassword = c} /><br /><br />
    <input type="text" id="firstName" placeholder="First Name" ref={(c) => firstName = c}  /><br /><br />
    <input type="text" id="lastName" placeholder="Last Name" ref={(c) => lastName = c}  /><br /><br />
    <input type="email" id="email" placeholder="E-mail" ref={(c) => email = c}  /><br /><br />
    <input type="submit" id="registerButton" class="buttons" value = "Register" onClick={doRegister} />
  </form>
<span id="loginResult">{message}</span>
</div>

);
};

export default RegisterForm;
