import React, {useState, Component} from 'react';
import '../components/Background.css';
import 'bootstrap/dist/css/bootstrap.css';

var email;
var code;

const initialState = {
    email: "",
    emailError: ""
};

class ForgotPassword extends Component {
    state = initialState;
    code = crypto.randomBytes(4).toString('hex');

    handleSubmit = event => {
        event.preventDefault();
        const isValid = this.validate();
        if (isValid) {
        alert(code);
        console.log(this.state);
        this.setState(initialState);
        window.location.href = '/';
        }
    };

    validate = () => {
        let emailError = "";

        if(!this.state.email) {
            emailError = "Invalid Email";
        }

        if(!((/[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,15}/i).test(this.state.email))){
            emailError = "Invalid Email";
        }

        if(emailError){
            this.setState({emailError});
            return false;
        }

        return true;
    };

    handleChange = event => {
        const isCheckbox = event.target.type === "checkbox";
        this.setState({
          [event.target.name]: isCheckbox
            ? event.target.checked
            : event.target.value
        });
      };

      render(){
        return(
            <div id="forgotPasswordDiv">
                <div class="container">
                <div class="row">
                <div class="col-lg-6 col-md-7 mx-auto">
                <form onSubmit={this.handleSubmit} class="form-signin">
                <div class="text-center mb-4"></div>
    
                <div class="form-label-group">
                    <input value={this.state.email} name = "email" type="email" id="email" class="form-control" placeholder="Email" required="" autofocus="" ref={(c) => email = c} onChange={this.handleChange}/>
                    <label for="email">Email</label>
                    <div className ="errorMessage"> {this.state.emailError} </div>
                </div>
    
                <button class="btn btn-lg btn-secondary btn-block" type="submit">Reset</button>
                </form>
                </div>
                </div>
                </div>
            </div>
        );
    }
}

export default ForgotPassword;
