import React, {useState, Component} from 'react';
import '../components/Background.css';
import NavigationBar from '../components/NavigationBar';
import 'bootstrap/dist/css/bootstrap.css';
import md5 from '../components/md5'
const BASE_URL = 'https://cop4331mern.herokuapp.com/';

var email;
var password;
var hashedPassword;

const initialState = {
    email: "",
    password: "",
    emailError: "",
    passwordError: ""
  };

  const [message,setMessage] = useState('');

class LoginForm extends Component {
	state = initialState;

    handleSubmit = async event => {
        event.preventDefault();
        const isValid = this.validate();
        if (isValid) {
        hashedPassword = md5(this.state.password);

        var js = '{"email":"'
            + email.value
            + '","password":"'
            + hashedPassword.value +'"}';

        try
        {    
            const response = await fetch(BASE_URL + 'api/Login',
                {method:'POST',body:js,headers:{'Content-Type': 'application/json'}});

            var res = JSON.parse(await response.text());

            if( res.id <= 0 )
            {
                setMessage('Email/Password combination incorrect.');
            }
            else
            {
                var user = {email:res.email, password: res.hashedPassword}
                localStorage.setItem('user_data', JSON.stringify(user));

                setMessage('');
                window.location.href = '/cards';
            }
        }
        catch(e)
        {
            alert(e.toString());
            return;
        }    



        console.log(this.state);
        alert(email.value + ' ' + password.value + ' ' + hashedPassword);
        this.setState(initialState);
        window.location.href = '/logged';
        }
    };

    validate = () => {
        let emailError = "";
        let passwordError = "";

        if(!this.state.email) {
            emailError = "Invalid Email";
        }

        if(!((/[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,15}/i).test(this.state.email))){
            emailError = "Invalid Email";
        }

        if(!this.state.password){
            passwordError = "Invalid Password";
        }

        if(emailError || passwordError){
            this.setState({emailError, passwordError});
            return false;
        }
        
        return true;
    };
	
      handleChange = event => {
        const isCheckbox = event.target.type === "checkbox";
        this.setState({
          [event.target.name]: isCheckbox
            ? event.target.checked
            : event.target.value
        });
      };
	
	render(){
		return (
			<div id="loginDiv">
            <NavigationBar />
			<div class="container">
			<div class="row">
			<div class="col-lg-6 col-md-7 mx-auto">
            <form onSubmit={this.handleSubmit} class="form-signin">
            <div class="text-center mb-4">
            <br /><h1>Login</h1>
            </div>
			
			<div class="form-label-group">
				<input value={this.state.email} name="email" type="email" id="email" class="form-control" placeholder="Email" required="" autofocus="" ref={(c) => email = c} onChange={this.handleChange}/>
                <label for="email">Email</label>
				<div className ="errorMessage"> {this.state.emailError} </div>
            </div>

            <div class="form-label-group">
                <input value={this.state.password} name="password" type="password" id="password" class="form-control" placeholder="Password" required="" ref={(c) => password = c} onChange={this.handleChange}/>
                <label for="password">Password</label>
                <div className ="errorMessage"> {this.state.passwordError} </div>
            </div>  
			
			<button class="btn btn-lg btn-secondary btn-block" type="submit">Login</button>
            </form>
            <span id = "loginResult">{message}</span>
            </div>
            </div>
            </div>

            <div className = "ResetPassword">
                <a href = "/forgotpassword"> Forgot password? </a>
            </div>
            <div className = "ResetPassword">
                <a href = "/register"> New User? Register Here.</a>
            </div>

            </div>
		);
	}
}

export default LoginForm;
