import React, {useState, Component} from 'react';
import '../components/Background.css';
import 'bootstrap/dist/css/bootstrap.css';

var code;

class Verification extends Component {
doVerify = event => {

    event.preventDefault();
    alert(code.value + ' ');
}
render(){
    return(
        <div id="verificationDiv">
			<div class="container">
			<div class="row">
			<div class="col-lg-6 col-md-7 mx-auto">
            <form onSubmit={doVerify} class="form-signin">
            <div class="text-center mb-4"></div>

            <div class="form-label-group">
				<input type="text" id="code" class="form-control" placeholder="Verification Code" ref={(c) => code = c}/>
                <label for="code">Verification Code</label>
            </div>

            <button class="btn btn-lg btn-secondary btn-block" type="submit">Verify</button>
            </form>
            </div>
            </div>
            </div>
        </div>
    );
    }
}

export default Verification;
