import React from 'react';
import NavigationBar from '../components/NavigationBarLogged';

var id;
var firstName;
var lastName;
var Intro;
var CS1;
var CS2;
var Total;

function readCookie()
{
	var data = document.cookie;
	var splits = data.split(",");
	for(var i = 0; i < splits.length; i++) 
	{
		var thisOne = splits[i].trim();
		var tokens = thisOne.split("=");
        if(tokens[0] == "id"){
            id = parseInt(tokens[1].trim());
        }
        else if( tokens[0] == "firstName" )
		{
			firstName = tokens[1];
		}
		else if( tokens[0] == "lastName" )
		{
			lastName = tokens[1];
		}
		else if( tokens[0] == "Intro" )
		{
			Intro = tokens[1];
        } 
        else if (tokens[0] == "CS1")
        {
            CS1 = tokens[1];
        } 
        else if (tokens[0] == "CS2")
        {
            CS2 = tokens[1];
        } 
        else if(tokens[0] == "Total")
        {
            Total = tokens[1];
        }
	}
}


const LoggedInPage = () => {
    document.addEventListener('DOMContentLoaded', function() {
        readCookie();
    }, false);
    return(
        <div>
            <NavigationBar />
          <br/>  <h1> Logged In </h1> <br/>
          <p> You are logged in as {this.state.firstName} {this.state.lastName}. </p>
        </div>

    );
};

export default LoggedInPage;
