import React, { useState, Component } from 'react';
import '../components/Background.css';
import NavigationBar from '../components/NavigationBar';
import 'bootstrap/dist/css/bootstrap.css';
import md5 from '../components/md5'

var email;
var password;
var hashedPassword;
var id;
var firstName;
var lastName;
var Intro;
var CS1;
var CS2;
var Total;

const initialState = {
    email: "",
    password: "",
    emailError: "",
    passwordError: "",
};

function saveCookie()
{
	var minutes = 20;
	var date = new Date();
	date.setTime(date.getTime()+(minutes*60*1000));	
	document.cookie = "dummyID=" + id + ",dummyFirstName=" + firstName + ",dummyLastName=" + lastName + ",dummyIntro=" + Intro + ",dummyCS1=" + CS1 + ",dummyCS2=" + CS2 + ",dummyTotal=" + Total + ";expires=" + date.toGMTString();
}

class LoginForm extends Component {
    state = initialState;

    constructor(props){
        super(props);
        this.state = {
            ID: "",
            dummyID: "",
            firstName: "",
            dummyFirstName: "",
            lastName: "",
            dummyLastName: "",
            Intro: [],
            dummyIntro:[],
            CS1: [],
            dummyCS1: [],
            CS2: [],
            dummyCS2:[],
            Total: [],
            dummyTotal: [],
            error: ""
        };
    }

    handleSubmit = async event => {
        event.preventDefault();
        const isValid = this.validate();
        if (isValid) {
            hashedPassword = md5(this.state.password);
            var url = 'https://cop4331mern.herokuapp.com/api/getLogin';
            var postLogin =
            {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ email: email.value, password: password.value }) //change to hashed
            }
            
            fetch(url, postLogin)
                .then(res => res.json())
                .then(json => this.setState({
                    dummyID: json.ID, dummyFirstName: json.firstName, dummyLastName: json.lastName, dummyIntro: json.intro, dummyCS1: json.CS1,
                    dummyCS2: json.CS2, dummyTotal: json.Total, error: json.error
                })
                );
                saveCookie();
                console.log(this.state);
                alert(email.value + ' ' + password.value);
                this.setState(initialState);
                window.location.href = '/logged';
            }
        }

    validate = () => {
        let emailError = "";
        let passwordError = "";

        if (!this.state.email) {
            emailError = "Invalid Email";
        }

        if (!((/[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,15}/i).test(this.state.email))) {
            emailError = "Invalid Email";
        }

        if (!this.state.password) {
            passwordError = "Invalid Password";
        }

        if (emailError || passwordError) {
            this.setState({ emailError, passwordError });
            return false;
        }
        return true;
    };

    handleChange = event => {
        const isCheckbox = event.target.type === "checkbox";
        this.setState({
            [event.target.name]: isCheckbox
                ? event.target.checked
                : event.target.value
        });
    };


    render() {
        return (
            <div id="loginDiv">
                <NavigationBar />
                <div class="container">
                    <div class="row">
                        <div class="col-lg-6 col-md-7 mx-auto">
                            <form onSubmit={this.handleSubmit} class="form-signin">
                                <div class="text-center mb-4">
                                    <br /><h1>Login</h1>
                                    <h1> {this.state.error}, {this.state.dummyID}, {this.state.dummyFirstName}, {this.state.dummyLastName}</h1>
                                </div>

                                <div class="form-label-group">
                                    <input value={this.state.email} name="email" type="email" id="email" class="form-control" placeholder="Email" required="" autofocus="" ref={(c) => email = c} onChange={this.handleChange} />
                                    <label for="email">Email</label>
                                    <div className="errorMessage"> {this.state.emailError} </div>
                                </div>

                                <div class="form-label-group">
                                    <input value={this.state.password} name="password" type="password" id="password" class="form-control" placeholder="Password" required="" ref={(c) => password = c} onChange={this.handleChange} />
                                    <label for="password">Password</label>
                                    <div className="errorMessage"> {this.state.passwordError} </div>
                                </div>

                                <button class="btn btn-lg btn-secondary btn-block" type="submit">Login</button>
                            </form>
                        </div>
                    </div>
                </div>

                <div className="ResetPassword">
                    <a href="/forgotpassword"> Forgot password? </a>
                </div>
                <div className="ResetPassword">
                    <a href="/register"> New User? Register Here.</a>
                </div>

            </div>
        );
    }
}

export default LoginForm;
