import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.css';

var ID;

function getCookie(cname) {
  var name = cname + "=";
  var ca = document.cookie.split(';');
  for (var i = 0; i < ca.length; i++) {
    var c = ca[i];
    while (c.charAt(0) === ' ') {
      c = c.substring(1);
    }
    if (c.indexOf(name) === 0) {
      return c.substring(name.length, c.length);
    }
  }
  return "";
}

class NavigationBar extends Component {
  handleAccount = event => {
    event.preventDefault();
    ID = getCookie('ID');

    if (ID === "") { //not logged in
      window.location.href = '/login';
    } else {
      window.location.href = '/myaccount';
    }
  }

  render() {
    return (
      <>
        <nav className="navbar navbar-expand-sm bg-dark navbar-dark">
          <a className="navbar-brand" href="/">
            <img src={require('./triviacrevice2.png')} className="image-responsive" alt="logo" />
  Trivia Crevice
  </a>

          <ul className="navbar-nav ml-auto">
            <li className="nav-item">
              <a className="nav-link" href="https://play.google.com/store?hl=en_US">Download</a>
            </li>

            <li className="nav-item">
              <a className="nav-link" href="/register">Register</a>
            </li>

            <li className="nav-item">
              <a onClick = {this.handleAccount} className="nav-link" href="#">Login</a>
            </li>

            <li className="nav-item">
              <a onClick={this.handleAccount} class="nav-link" href="#">My Account</a>
            </li>

          </ul>

        </nav>
      </>
    );
  };
}

export default NavigationBar;
