import React, {useState} from 'react';
import '../components/Background.css';
import 'bootstrap/dist/css/bootstrap.css';

const BASE_URL = 'https://cop4331mern.herokuapp.com/';

function LoginForm() {
    var loginName;
    var loginPassword;

    const [message,setMessage] = useState('');
    const doLogin = async event => 
    {
        event.preventDefault();
        alert( loginName.value + ' ' + loginPassword.value );
    }

return(
 <div id="loginDiv">
     <nav class="navbar navbar-inverse">
  <div class="container-fluid">
    <div class="navbar-header">
      <a class="navbar-brand" href="#">Trivia Crevice</a>
    </div>
    <ul class="nav navbar-nav">
      <li class="active"><a href="/">Home</a></li>
      <li><a href="/register">Register</a></li>
      <li><a href="/login">Login</a></li>
    </ul>
  </div>
</nav>
  <form onSubmit={doLogin}>
<span id="inner-title">PLEASE LOG IN</span><br />
<input type="text" id="loginName" placeholder="Username" ref={(c) => loginName = c}  /><br />
<input type="password" id="loginPassword" placeholder="Password" ref={(c) => loginPassword = c} /><br />
  <input type="submit" id="loginButton" class="buttons" value = "Do It" onClick={doLogin} />
  <button type="button" class="btn btn-primary">Primary</button>

  </form>
<span id="loginResult">{message}</span>
</div>

);
};

export default LoginForm;
