import React, {useState, Component} from 'react';
import '../components/Background.css';
import NavigationBar from '../components/NavigationBar';
import 'bootstrap/dist/css/bootstrap.css';
const BASE_URL = 'https://cop4331mern.herokuapp.com/';

var loginName;
var firstName;
var lastName;
var email;
var password;
var confirmPassword;

class RegisterForm extends Component {
  state = {
  loginName: "",
  firstName: "",
  lastName: "",
  email: "",
  password: "",
  confirmPassword: ""
  };

	submitHandler = event => {
      event.preventDefault();
      event.target.className += " was-validated";
    };
	
	changeHandler = event => {
      this.setState({ [event.target.name]: event.target.value });
    };
	
	render(){
		return (
			<div id="loginDiv">
            <NavigationBar />
			<div class="container">
			<div class="row">
			<div class="col-lg-6 col-md-7 mx-auto">
            <form onSubmit={this.submitHandler} class="form-signin" className="needs-validation" noValidate>
            <div class="text-center mb-4">
            <br /><h1>Register</h1>
            </div>
			
			<div class="form-label-group">
				<input value={this.state.loginName} name="loginName" type="text" id="loginName" class="form-control" placeholder="Username" required="" autofocus="" ref={(c) => loginName = c} onChange={this.changeHandler} required/>
                <label for="loginName">Username</label>
				<div className = "invalid-feedback"> Invalid Username </div>
				<div className = "valid-feedback">  </div>
            </div>

            <div class="form-label-group">
				<input value={this.state.firstName} name="firstName" type="text" id="firstName" class="form-control" placeholder="First Name" required="" autofocus="" ref={(c) => firstName = c} onChange={this.changeHandler} required/>
                <label for="firstName">First Name</label>
				<div className = "invalid-feedback"> Invalid First Name </div>
				<div className = "valid-feedback">  </div>
            </div>

            <div class="form-label-group">
				<input value={this.state.lastName} name="lastName" type="text" id="lastName" class="form-control" placeholder="Last Name" required="" autofocus="" ref={(c) => lastName = c} onChange={this.changeHandler} required/>
                <label for="lastName">Last Name</label>
				<div className = "invalid-feedback"> Invalid Last Name </div>
				<div className = "valid-feedback">  </div>
            </div>

            <div class="form-label-group">
				<input value={this.state.email} name="email" type="email" id="email" class="form-control" placeholder="Email" required="" autofocus="" ref={(c) => email = c} onChange={this.changeHandler} required/>
                <label for="email">Email</label>
				<div className = "invalid-feedback"> Invalid Email </div>
				<div className = "valid-feedback">  </div>
            </div>

            <div class="form-label-group">
                <input value={this.state.password} name="password" type="password" id="password" class="form-control" placeholder="Password" required="" ref={(c) => password = c} onChange={this.changeHandler} required />
                <div className = "invalid-feedback"> Invalid Password </div>
				<div className = "valid-feedback">  </div>
                <label for="password">Password</label>
            </div>   

            <div class="form-label-group">
                <input value={this.state.confirmPassword} name="confirmPassword" type="password" id="confirmPassword" class="form-control" placeholder="Confirm Password" required="" ref={(c) => confirmPassword = c} onChange={this.changeHandler} required />
                <div className = "invalid-feedback"> Invalid Password </div>
				<div className = "valid-feedback">  </div>
                <label for="confirmPassword"> Confirm Password</label>
            </div>   
			
			<button class="btn btn-lg btn-secondary btn-block" type="submit">Register</button>
            </form>
        </div>
        </div>
        </div>
        </div>
		);
	}
}

export default RegisterForm;
