import React, {useState, Component} from 'react';
import '../components/Background.css';
import 'bootstrap/dist/css/bootstrap.css';

function ForgotPassword() {
    var email;

const doForgotPassword = async event => {

    event.preventDefault();
    alert(email.value + ' ');
    window.location.href = '/forgotpassword';
};

    return(
        <div id="forgotPasswordDiv">
			<div class="container">
			<div class="row">
			<div class="col-lg-6 col-md-7 mx-auto">
            <form onSubmit={doForgotPassword} class="form-signin">
            <div class="text-center mb-4"></div>

            <div class="form-label-group">
				<input type="email" id="email" class="form-control" placeholder="Email" ref={(c) => email = c}/>
                <label for="email">Email</label>
            </div>

            <button class="btn btn-lg btn-secondary btn-block" type="submit">Reset</button>
            </form>
            </div>
            </div>
            </div>
        </div>
    );
}

export default ForgotPassword;
