import React, {useState} from 'react';
import '../components/Background.css';
import NavigationBar from '../components/NavigationBar';
import 'bootstrap/dist/css/bootstrap.css';

const BASE_URL = 'https://cop4331mern.herokuapp.com/';

function LoginForm() {
    var loginName;
    var loginPassword;

    const [message,setMessage] = useState('');
    const doLogin = async event => 
    {
        event.preventDefault();
        alert( loginName.value + ' ' + loginPassword.value );
    }

    return(
      <div id="loginDiv">
     <NavigationBar />
       <form onSubmit={doLogin}>
     <h1> LOGIN </h1><br />
     <input type="text" id="loginName" placeholder="Username" ref={(c) => loginName = c}  /><br /><br/>
     <input type="password" id="loginPassword" placeholder="Password" ref={(c) => loginPassword = c} /><br />
       <input type="submit" id="loginButton" class="buttons" value = "Login" onClick={doLogin} />
       <button type="button" class="btn btn-primary">Primary</button>
       </form>
     <span id="loginResult">{message}</span>
     </div>
     
     );
     };
     
     export default LoginForm;
