import React, {useState, Component} from 'react';
import '../components/Background.css';
import NavigationBar from '../components/NavigationBar';
import 'bootstrap/dist/css/bootstrap.css';
const BASE_URL = 'https://cop4331mern.herokuapp.com/';

var firstName;
var lastName;
var email;
var password;
var confirmPassword;

const initialState = {
  firstName: "",
  lastName: "",
  email: "",
  password: "",
  confirmPassword: "",
  firstNameError: "",
  lastNameError: "",
  emailError: "",
  passwordError: "",
  confirmPasswordError: ""
};

const regExp = RegExp(
    /^[a-zA-Z0-9]+@[a-zA-Z0-9]+\.[A-Za-z]+$/
);

class RegisterForm extends Component {
  state = initialState;

    handleSubmit = event => {
        event.preventDefault();
        const isValid = this.validate();
        if (isValid) {
        console.log(this.state);
        this.setState(initialState);
        }
    };

    validate = e => {
        const {value} = e.target;
        let firstNameError = "";
        let lastNameError = "";
        let emailError = "";
        let passwordError = "";
        let confirmPasswordError = "";

        if(!this.state.firstName){
            firstNameError = "Invalid FIrst Name";
        }

        if(!this.state.lastName){
            lastNameError = "Invalid Last Name";
        }

        if(!this.state.email) {
            emailError = "Invalid Email";
        }

        if(this.state.email = regExp.test(value)){
            emailError = "Invalid Email";
        }

        if(!this.state.password){
            passwordError = "Invalid Password";
        }

        if(!this.state.confirmPassword){
            confirmPasswordError = "Invalid Password";
        }

        if(this.state.password !== this.state.confirmPassword){
            confirmPasswordError = "Passwords Do Not Match";
        }

        if(firstNameError || lastNameError || emailError || passwordError || confirmPasswordError){
            this.setState({firstNameError, lastNameError, emailError, passwordError, confirmPasswordError});
            return false;
        }
        
        return true;
    };
	
      handleChange = event => {
        const isCheckbox = event.target.type === "checkbox";
        this.setState({
          [event.target.name]: isCheckbox
            ? event.target.checked
            : event.target.value
        });
      };
	
	render(){
		return (
			<div id="loginDiv">
            <NavigationBar />
			<div class="container">
			<div class="row">
			<div class="col-lg-6 col-md-7 mx-auto">
            <form onSubmit={this.handleSubmit} class="form-signin">
            <div class="text-center mb-4">
            <br /><h1>Register</h1>
            </div>
			
            <div class="form-label-group">
				<input value={this.state.firstName} name="firstName" type="text" id="firstName" class="form-control" placeholder="First Name" required="" autofocus="" ref={(c) => firstName = c} onChange={this.handleChange} required/>
                <label for="firstName">First Name</label>
				<div style = {{fontSize: 10, color: "red"}}> {this.state.firstNameError} </div>
            </div>

            <div class="form-label-group">
				<input value={this.state.lastName} name="lastName" type="text" id="lastName" class="form-control" placeholder="Last Name" required="" autofocus="" ref={(c) => lastName = c} onChange={this.handleChange} required/>
                <label for="lastName">Last Name</label>
				<div style = {{fontSize: 10, color: "red"}}> {this.state.lastNameError} </div>
            </div>

            <div class="form-label-group">
				<input value={this.state.email} name="email" type="email" id="email" class="form-control" placeholder="Email" required="" autofocus="" ref={(c) => email = c} onChange={this.handleChange} required/>
                <label for="email">Email</label>
				<div style = {{fontSize: 10, color: "red"}}> {this.state.emailError} </div>
            </div>

            <div class="form-label-group">
                <input value={this.state.password} name="password" type="password" id="password" class="form-control" placeholder="Password" required="" ref={(c) => password = c} onChange={this.handleChange} required />
                <label for="password">Password</label>
                <div style = {{fontSize: 10, color: "red"}}> {this.state.passwordError} </div>
            </div>   

            <div class="form-label-group">
                <input value={this.state.confirmPassword} name="confirmPassword" type="password" id="confirmPassword" class="form-control" placeholder="Confirm Password" required="" ref={(c) => confirmPassword = c} onChange={this.handleChange} required />
                <label for="confirmPassword"> Confirm Password</label>
                <div style = {{fontSize: 10, color: "red"}}> {this.state.confirmPasswordError} </div>
            </div>   
			
			<button class="btn btn-lg btn-secondary btn-block" type="submit">Register</button>
            </form>
        </div>
        </div>
        </div>
        </div>
		);
	}
}

export default RegisterForm;
