import React, {Component} from 'react'
import './App.css'
import Table from 'react-bootstrap/Table'
import { Dropdown, DropdownToggle, DropdownMenu, DropdownItem, DropdownButton} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css' // for the minimal css for table and buttons



const App = () => 
{
  const leader = 
  [
    {name: "this guy", category: "Intro to C", score: 10, percentage: ""},
    {name: "person", category: "Intro to C", score: 6, percentage: ""},
    {name: "dude", category: "Intro to C", score: 2, percentage: ""},
    {name: "someone", category: "Intro to C", score: 4, percentage: ""}
  ]

  leader.sort((a, b) => (a.score < b.score) ? 1 : -1)

  /*
  function ranking(this.props.index)
  { 
    index++;

    if (index == 1)
      return(<td>this is 1</td>)
    else if (index == 2)
      return(<td>this is 2</td>)
    else if (index == 3)
      return(<td>this is 3</td>)
    else
      return(<td>{index}</td>)
  }
  */
  const renderTable = (leader, index) =>
  {
    const bool1 = 12;
    const bool2 = 32; 


    return(
      <tr key = {index}>
        <td>{++index}</td>
        <td>{leader.name}</td>
        <td>{leader.category}</td>
        <td>{leader.score}</td>
        <td>{leader.percentage}</td>
      </tr>
    )
  }

  return (
  <div className = "App">
    <div class = 'row'>
    <div className = "header" class = 'col-md-5'></div>
    <div className = "header" class = 'text-left'>
      <h1 id='title'>Trivia Crevice</h1>
      </div>
    </div>
    <div class = 'row'>
      <div className = "dropDown" class = 'col-md-5'>
        <DropdownButton  class = 'text-left' id="dropdown-basic-button" title="Category" variant = 'success'>
          <Dropdown.Item id = 'ddOption' href="#/action-1">Intro to C</Dropdown.Item>
          <Dropdown.Item id = 'ddOption' href="#/action-2">CSI</Dropdown.Item>
          <Dropdown.Item id = 'ddOption' href="#/action-3">CSII</Dropdown.Item>
        </DropdownButton>
      </div>


      <div class = 'col-md-5'>
        <h1 id = 'title' class = 'text-left'> Leaderboard </h1>
      </div>
    </div>




    <Table borderd  hover>
      <thead>
        <tr>
          <th>Rank</th>
          <th>Name</th>
          <th>Category</th>
          <th>Score</th>
          <th>Percentage</th>
        </tr>
      </thead>
      <tbody>
       {leader.map(renderTable)}
      </tbody>
    </Table>
  </div>
  )

}

export default App;
