import React, {Component} from 'react'
import './App.css'
import Table from 'react-bootstrap/Table'
//import RenderPersonalStats from './RenderPersonalStats'
import { Dropdown, DropdownToggle, DropdownMenu, DropdownItem, DropdownButton} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css' // for the minimal css for table and buttons


// renders based on state and sends info based on which state to RenderPersonalStats
// first issue was capitalizing all functiona and component names in react
// second issue was acutally passing in the value when calling the function
// third issue was that even a single element array needs to be mapped, so don't have an object as a single array
// fourth issue is i forgot the 'personal' in props.personal.score
function RenderPersonal(props)
{
  if (props.category === "total")
  {
    return(<RenderPersonalStats name = {props.personal.name} 
                                category = "total" 
                                percentage = {props.personal.percentage}
                                score = {props.personal.score}/>)
  }
  else if (props.category === "introC")
  {
    return(<RenderPersonalStats name = {props.personal.name} 
                                category = "Intro to C" 
                                percentage = {props.personal.percentage}
                                score = {props.personal.score}/>)
  }
  else if (props.category === "cs1")
  {
    return(<RenderPersonalStats name = {props.personal.name} 
                                category = "CSI" 
                                percentage = {props.personal.percentage}
                                score = {props.personal.score}/>)
  }
  else if (props.category === "cs2")
  {
    return(<RenderPersonalStats name = {props.personal.name} 
                                category = "CSII" 
                                percentage = {props.personal.percentage}
                                score = {props.personal.score}/>)
  }
}

function RenderPersonalStats(props)
{

    return(

      <div>
           <Table borderd hover>
            <thead>
              <tr>
                <th>Name</th>
                <th>Category</th>
                <th>Score</th>
                <th>Percentage</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>{props.name}</td>
                <td>{props.category}</td>
                <td>{props.score}</td>
                <td>{props.percentage}</td>
              </tr>
            </tbody>
          </Table>
      </div>
    )
}

export default class App extends Component
{
  constructor(props)
  {
    super(props)
    this.state = {
        leaders:
        [
          {name: "this guy", category: "Intro to C", score: 10, percentage: ""},
          {name: "person", category: "Intro to C", score: 6, percentage: ""},
          {name: "dude", category: "Intro to C", score: 2, percentage: ""},
          {name: "someone", category: "Intro to C", score: 4, percentage: ""}
        ],
        personal: {name: "me", intro:"intro", cs1:"cs1", cs2: "cs2", total: "total", score: 230, percentage: 100},
        category: "total",
        deleteValue: 1,
        blah: 'name'
    }
  }

  setIntro()
  {
    this.setState({category: "Intro to C"})
  }



  render()
  {

    return(

    <div className = 'App'>

    <h1 id = 'title'>Trivia Crevice</h1> 
    <h1 id = 'title' class = 'spacer'>Leaderboards</h1>

    <div class = 'row'>

      <div class = 'col-md-5'><h2>Personal Stats</h2></div>

      <div id = "drop" className = "dropDown" class = 'col-md-6'>
        <DropdownButton  id="dropdown-basic-button" title="Category" variant = 'success'>
          <Dropdown.Item id = 'ddOption' onClick = {this.setIntro}>Intro to C</Dropdown.Item>
          <Dropdown.Item id = 'ddOption' href="#/action-2">CSI</Dropdown.Item>
          <Dropdown.Item id = 'ddOption' href="#/action-3">CSII</Dropdown.Item>
        </DropdownButton>
      </div>      
    </div>

    <RenderPersonal category = {this.state.category} personal = {this.state.personal}/>




   </div>
    )
  }
}
