import React, {Component} from 'react'
import './App.css'
import Table from 'react-bootstrap/Table'
//import RenderPersonalStats from './RenderPersonalStats'
import { Dropdown, DropdownToggle, DropdownMenu, DropdownItem, DropdownButton} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css' // for the minimal css for table and buttons


// renders based on state and sends info based on which state to RenderPersonalStats
function renderPersonal(props)
{
  if (props.currentCategory === "total")
  {
    return(<div><h1>cat is total</h1></div>)
  }
  else
  {
    return(<div><h1>cat is not total</h1></div>)
  }
}

function RenderPersonalStats(props)
{
    const personal = props.personal

    return(

      <div>
          <h2>Personal Stats</h2>

           <Table borderd hover>
            <thead>
              <tr>
                <th>Name</th>
                <th>Category</th>
                <th>Score</th>
                <th>Percentage</th>
              </tr>
            </thead>

          </Table>
      </div>
    )
}

export default class App extends Component
{
  constructor(props)
  {
    super(props)
    this.state = {
        leaders:
        [
          {name: "this guy", category: "Intro to C", score: 10, percentage: ""},
          {name: "person", category: "Intro to C", score: 6, percentage: ""},
          {name: "dude", category: "Intro to C", score: 2, percentage: ""},
          {name: "someone", category: "Intro to C", score: 4, percentage: ""}
